import React, { useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Link } from "react-router-dom";
import axios from "axios";
import doctorimage from "../Images/doctor.jpg";

const MyDoctors = () => {
  const [doctors, setDoctors] = useState([]);
  const [name, setName] = useState("");
  const [username, setUsername] = useState("");
  const [hospitalName, setHospitalName] = useState("");
  const [department, setDepartment] = useState("");
  const [designation, setDesignation] = useState("");
  const [area, setArea] = useState("");

  const [query, setQuery] = useState();

  const loadDoctors = async () => {
    const url = "http://localhost:8000/patients/doctor-list";

    const res = axios
      .get(url, { params: query })
      .then((response) => {
        
        setDoctors(response.data.doctors);
        console.log(response.data.doctors[0].consultency[0]);
       
      })
      .catch((error) => {
        console.log(error);
      });
   
  };

  const doDoctorSearch = () => {
    setQuery({
      department: department,
      designation: designation,
      name: name,
      area: area,
    });
  };

  useEffect(() => {
   
    loadDoctors();
  }, [query]);

  return (
    <>
     
    <div className="max-w-[1320px] mx-auto">
      <h1 className="text-3xl font-bold text-center mt-10">My Doctors</h1>
    </div>
  
    <div class="grid grid-cols-4 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4 mx-10 my-10 ">
    
  {doctors && doctors.length > 0 ? (
    doctors.map((doctor, index) => (
      <Link to='/FindDoctor'>
      <div key={index} class="bg-white shadow-md rounded-lg overflow-hidden">
        <img class="w-50 h-50 object-cover object-center" src={doctorimage} alt="Card Image"/>
        <div class="p-4">
          <h2 class="font-bold text-xl mb-2">{doctor.name}</h2>
          <p class="text-gray-700">{doctor.department}</p>
          <p class="text-gray-700">{doctor.designation}</p>
          <p class="text-gray-700">Clinic: {doctor.consultency[0] ? doctor.consultency[0].clinic_name : 'Not available'}</p>
          <h5>Visiting Hour :</h5>
          {doctor.consultency[0] && doctor.consultency[0].days ? (
            <p class="text-gray-700">
              Days: {doctor.consultency[0].days.map(day => day.day).join(', ')}
            </p>
          ) : (
            <p class="text-gray-700">Days: Not available</p>
          )}
          <p class="text-gray-700">Start Time: {doctor.consultency[0] ? doctor.consultency[0].start_time : 'Not available'}</p>
          <p class="text-gray-700">End Time: {doctor.consultency[0] ? doctor.consultency[0].end_time : 'Not available'}</p>
          <p class="text-gray-700">Room: {doctor.consultency[0] ? doctor.consultency[0].room : 'Not available'}</p>
        </div>
      </div>
      </Link>
    ))
  ) : (
    <p>NO doctor available</p>
  )}
</div>

   

  
  
    </>
    
  )
}

export default MyDoctors
