import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const UpdateProfilePage = () => {
  // Assuming you fetch the user's ID from somewhere, like context or a global state.
  const userId = "user123"; // Example user ID

  const [doctorData, setDoctorData] = useState({
    username: "",
    name: "",
    email: "",
    phoneNumber: "",
    description: "",
    hospitalName: "",
    department: "",
    degree: [],
    designation: "",
    consultency: [],
  });

  const navigate = useNavigate();
  const profileuser = localStorage.getItem("username");
  const requestingUsername = profileuser;
  useEffect(() => {
    // Fetch the existing user data
    const fetchData = async () => {
      try {
        //

        const response = await axios.get(
          `http://localhost:8000/doctors/profile?username=${profileuser}&requesting_username=${requestingUsername}`
        );
        const { doctor } = response.data;
        console.log("get doctor data");
        console.log(response.data);
        setDoctorData({
          username: doctor.username,
          name: doctor.name,
          email: doctor.email,
          phoneNumber: doctor.phone_number,
          description: doctor.description,
          hospitalName: doctor.hospital_name,
          department: doctor.department,
          degree: doctor.degree,
          designation: doctor.designation,
          consultency: doctor.consultency,
        });
        console.log(doctorData);
       
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };
    fetchData();
  }, [userId]);


  

  const handleChange = (e) => {
    setDoctorData({ ...doctorData, [e.target.name]: e.target.value });
  };

  const handleDegreesChange = (e) => {
    const degreesArray = e.target.value.split(',').map(degree => {
      const [degreeName, speciality = ''] = degree.trim().split('|').map(s => s.trim());
      return { degree: degreeName, speciality };
    });
    setDoctorData({ ...doctorData, degree: degreesArray });
  };

  const degreesString = doctorData.degree.map(degreeObj => `${degreeObj.degree}${degreeObj.speciality ? ' | ' + degreeObj.speciality : ''}`).join(", ");


  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(
        // Changed from put to patch
        `http://localhost:3000/doctor/profile-update/?doctor-username=${profileuser}`,
        doctorData
      );

      //console.log(response.data);
      alert("Profile updated successfully.");
      navigate("/profileviewpage"); // Redirect to the profile page or wherever appropriate
    } catch (error) {
      console.error("There was an error updating the profile:", error);
      alert("An error occurred during profile update. Please try again later.");
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-transparent">
      <div className="bg-white p-20 rounded shadow-md">
        <h2 className="text-2xl font-bold mb-4 text-center">Update Profile</h2>
        <form onSubmit={handleSubmit}>
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="username">
              Username:
            </label>
            <input
              id="username"
              type="text"
              name="username"
              value={doctorData.username}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter your username"
            />
          </div>
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="username">
              Full Name:
            </label>
            <input
              id="name"
              type="text"
              name="name"
              value={doctorData.name}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter your name"
            />
          </div>
          {/* Repeat similar structure for other fields */}
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="hospitalName">
              Hospital Name:
            </label>
            <input
              id="hospitalName"
              type="text"
              name="hospitalName"
              value={doctorData.hospitalName}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter hospital name"
            />
          </div>
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="department">
              Department:
            </label>
            <input
              id="department"
              type="text"
              name="department"
              value={doctorData.department}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter department"
            />
          </div>
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="department">
              Department:
            </label>
            <input
              id="department"
              type="text"
              name="department"
              value={doctorData.department}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter department"
            />
          </div>
          
          {/* Degree is an array, you can handle it as per your requirement */}
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="degree">
              Degrees and Specialties:
            </label>
            <input
              id="degree"
              type="text"
              name="degree"
              value={degreesString}
              onChange={handleDegreesChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Format: Degree | Specialty, Degree | Specialty"
            />
          </div>
          {/* Repeat similar structure for other fields */}
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="clinicName">
              Clinic Name:
            </label>
            <input
              id="clinicName"
              type="text"
              name="clinicName"
              value={doctorData.clinicName}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter clinic name"
            />
          </div>
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="username">
              Phone Number:
            </label>
            <input
              id="phone number"
              type="text"
              name="phone number"
              value={doctorData.phoneNumber}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter your phone number"
            />
          </div>
          {/* Days is an array, you can handle it as per your requirement */}
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="days">
              Days:
            </label>
            <input
              id="days"
              type="text"
              name="days"
              value={doctorData.days}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter days"
            />
          </div>
          {/* Repeat similar structure for other fields */}
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="startTime">
              Start Time:
            </label>
            <input
              id="startTime"
              type="text"
              name="startTime"
              value={doctorData.startTime}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter start time"
            />
          </div>
          <div className="mb-4 flex items-center">
            <label className="w-1/3" htmlFor="endTime">
              End Time:
            </label>
            <input
              id="endTime"
              type="text"
              name="endTime"
              value={doctorData.endTime}
              onChange={handleChange}
              className="w-2/3 px-3 py-2 border focus:outline-none focus:border-blue-500 rounded"
              placeholder="Enter end time"
            />
          </div>
          {/* Repeat similar structure for other fields */}
          <div>
            <button
              type="submit"
              className="w-full py-2 px-3 bg-blue-500 text-white font-bold rounded hover:bg-blue-700"
            >
              Update Profile
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default UpdateProfilePage;
