    // doctor_name: formData.doctorName,
    // specialist: formData.doctorSpecialist,
    // notes: formData.notes,
    // date: "2024-01-12",
    // symptoms: [
    //   { symptom: "Chest pain" },
    //   { symptom: "Shortness of breath" }
    // ],
    // tests: [
    //   { test_name: "ECG" },
    //   { test_name: "Blood test" }
    // ],
    // diagnoses: [
    //   { disease: "Possible heart attack" }
    // ],
    // advices: [
    //   { advice: "Rest and avoid physical activity" }
    // ],
    // medicines: [
    //   {
    //     medicine_name: "Aspirin",
    //     duration: 7,
    //     meal_time: "before",
    //     breakfast: true,
    //     lunch: false,
    //     dinner: false
    //   },
    //   {
    //     medicine_name: "Nitroglycerin",
    //     duration: 7,
    //     meal_time: "after",
    //     breakfast: false,
    //     lunch: false,
    //     dinner: true
    //   }
    // ]
  
//   const initialSymptoms = response.symptoms.map(symptom => symptom.symptom);
// setSymptoms(initialSymptoms);

// // Set Tests
// const initialTests = response.tests.map(test => test.test_name);
